import React, { useEffect } from "react";
import { Table } from "reactstrap";


import base_url from "../api/Service";
import axios from 'axios';
import { toast } from "react-toastify";
import { useState } from "react";
import Customer from "./Customer";

function AllCustomers() {


    const [customers, setCustomer] = useState([])

    useEffect(() => {
        document.title = "Customer Report || Property Booking Management System"
    }, [])

    const getAllCustomerFromServer = () => {
        axios.get(`${base_url}/customers/`).then(
            (response) => {
                console.log(response);
                console.log(response.data);
                toast.success("Customers has been Loaded");
                setCustomer(response.data)
            },
            (error) => {
                console.log(error);
                toast.error("Something Went Wrong");
            }
        );
    };

    useEffect(() => {
        getAllCustomerFromServer();
    }, [])

    return (
        <div className="ml-3">
            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                <h2>Customers Reports</h2>
            </div>

            {
                customers.length > 0 ? customers.map((cust) => (
                    <Customer customer= {cust}/>
                )) : "No Customer"
            }

            <Table className="m-3" striped >
                <thead>
                    <tr>
                        <th>Customer Id</th>
                        <th>Customer Name</th>
                        <th>Mobile Number</th>
                        <th>City</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        customers.length > 0 ? customers.map((customer) => (
                            <tr>
                                <th scope="row">{customer.custID}</th>
                                <td>{customer.CustName}</td>
                                <td>{customer.CustMobNo}</td>
                                <td>{customer.CustCity}</td>
                            </tr>
                        )) : "No Customer"
                    }


                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Larry</td>
                        <td>the Bird</td>
                        <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
        </div>
    )
}

export default AllCustomers
