import React, { useEffect } from "react";
import { Table } from "reactstrap";


import base_url from "../api/Service";
import axios from 'axios';
import { toast } from "react-toastify";
import { useState } from "react";

function AllCustomers() {

    
    const [customers,setCustomer]=useState([])

    useEffect(() => {
        document.title = "Customer Report || Property Booking Management System"
    }, [])

    const getAllCustomerFromServer = () => {
        axios.get(`${base_url}/customers/`).then(
            (response) => {
                toast.success("Customers List has been Loaded",{
                    toastId: "loadedtoast"
                  });
                setCustomer(response.data)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down",{
                    toastId: "errortoast"
                  });
            }
        );
    };

    useEffect(() => {
        getAllCustomerFromServer();
    }, [])

    return (
        <div className="ml-3">
            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                <h2>Customers Reports</h2>
            </div>

            <Table className="tableContainer m-3" striped >
                <thead>
                    <tr>
                        <th>Customer Id</th>
                        <th>Customer Name</th>
                        <th>Mobile Number</th>
                        <th>City</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        customers.length>0?customers.map((customer,i)=>(
                        <tr key={i}>
                            <th scope="row">{customer.custID}</th>
                            <td>{customer.custName}</td>
                            <td>{customer.custMobNo}</td>
                            <td>{customer.custCity}</td>
                        </tr>
                        )):<tr><td colSpan={4}>No Customers Found or Server Down</td></tr>
                    }
                </tbody>
            </Table>
        </div>
    )
}

export default AllCustomers
