import axios from "axios";
import React, { useEffect } from "react";
import base_url from "../api/Service";

function AddPayment() {

    useEffect(()=> {
        document.title="Add Payment || Property Booking Management System"
    },[])

    const [payid, setPayId] = useState({})

    const getPayID = () => {

        axios.get(`${base_url}/payments/maxid`).then(
            (response) => {
                setPayId(response.data + 1)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down", {
                    toastId: "errortoast"
                });
            }
        );
    }

    useEffect(() => {
        getPayID();
    }, [])

    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">
                    
                    <div className="w-100">
                        <form className="justify-content-center text-center" action=" " method="post" id="payment_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Payment Details</h2>
                            </div>
                            <input name="PayID" placeholder="Payment ID" className="form-control m-3" disabled type="text" value={payid}/>
                            
                            <div className="form-group ">
                                <select className="form-control m-3" id="customerlist">
                                    <option>Select Booking ID</option> 
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </select>
                            </div>
                            
                            <input name="CustName" disabled placeholder="Customer's Name" className="form-control m-3" type="text" />
                            <input name="PropRate" disabled placeholder="Rate" className="form-control m-3"  type="text" />
                            <input name="AmountPaid" disabled placeholder="Amount Paid" className="form-control m-3"  type="text" />
                            <input name="AmountRemaining" disabled placeholder="Remaining Amount" className="form-control m-3"  type="text" />
                            <input name="AmountPaying" placeholder="Amount Paying Now" className="form-control m-3"  type="text" />

                            <button type="submit" className="btn btn-outline-success">Add Payment</button>
                        </form>
                    </div>
                    
                </div>
            </div>
        </div>

    )
}

export default AddPayment
