import axios from "axios";
import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import base_url from "../api/Service";

function AddProperty() {

    useEffect(()=> {
        document.title="Add Property || Property Booking Management System"
    },[])

    const [property, setProperty] = useState({})
    const [propid, setPropId] = useState({})
    const [cityList, setCityList] = useState({})

    const getPropID = () => {

        axios.get(`${base_url}/property/maxid`).then(
            (response) => {
                setPropId(response.data + 1)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down", {
                    toastId: "errortoast"
                });
            }
        );

    }

    const getCityList = () => {

        axios.get(`${base_url}/cities/`).then(
            (response) => {
                setCityList(response.data)
            },
            (error) => {
                console.log(error);
            }
        );
    }

    useEffect(() => {
        getPropID();
        getCityList();
    }, [])

    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">
                    
                    <div className="w-100">

                        <form className="justify-content-center text-center" action=" " method="post" id="property_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Property Details</h2>
                            </div>
                            <input id="PropID" placeholder="Property ID" className="form-control m-3" type="text" disabled value={propid} />
                            <input name="PropName" placeholder="Enter Property Name" className="form-control m-3" type="text" />
                            <input name="PropAddress" placeholder="Property Address" className="form-control m-3" type="text" />
                            <div className="form-inline row ">
                                <div className="col-6">
                                    <select className="form-control w-100 ml-3" id="city" onChange={(e) => {
                                        setProperty({ ...property, city: e.target.value })
                                    }}>
                                        <option>Select City</option>

                                        {
                                            cityList.length > 0 ? cityList.map((city, i) => (

                                                <option key={i}>{city.city}</option>


                                            )) : <option>Server Down or No City Found</option>
                                        }

                                    </select>
                                </div>
                                <div className="col-6">
                                    <select className="form-control w-100 ml-3" id="state" onChange={(e) => {
                                        setProperty({ ...property, state: e.target.value })
                                    }}>
                                        <option>Select State</option>
                                        <option>Maharashtra</option>
                                    </select>

                                </div>

                            </div>
                            <input name="Pincode" placeholder="Pin Code" className="form-control m-3" type="text" />
                            <input name="PropRate" placeholder="Enter Property Rate" className="form-control m-3" type="text"  />
                            <button type="submit" className="btn btn-outline-success mx-3">Add Property</button>
                            <button type="reset" className="btn btn-outline-danger mx-3">Clear</button>
                        </form>
                    </div>
                    
                </div>
            </div>
        </div>

    )
}

export default AddProperty
