import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { Table } from "reactstrap";
import base_url from "../api/Service";

function AllBookings() {

    useEffect(()=> {
        document.title="Booking Report || Property Booking Management System"
    },[])

    const [bookings, setBookings] = useState([])

    const getAllBookingFromServer = () => {
        axios.get(`${base_url}/bookings/`).then(
            (response) => {
                toast.success("Booking List has been Updated", {
                    toastId: "loadedtoast"
                });
                setBookings(response.data)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down", {
                    toastId: "errortoast"
                });
            }
        );
    };

    useEffect(() => {
        getAllBookingFromServer();
    }, [])

    return (
        <div className="ml-3">
            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                <h2>Bookings Reports</h2>
            </div>
            <Table className="table table-bordered table-striped mb-0 w-100" id="CustomerTable" striped >
                    <thead>
                        <tr>
                            <th className="col-2" scope="row">Booking Id</th>
                            <th className="col-3">Customer</th>
                            <th className="col-3">Property</th>
                            <th className="col-2">Rate</th>
                            <th className="col-2">Booking Amount</th>
                            <th className="col-2">Booking Date</th>
                            <th className="col-2">Update</th>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            bookings.length > 0 ? bookings.map((booking, i) => (
                                <tr key={i}>
                                    <th className="text-center" scope="row">{booking.bookID}</th>
                                    <td>{booking.cName}</td>
                                    <td>{booking.pName}</td>
                                    <td>{booking.rate}</td>
                                    <td>{booking.bookingAmount}</td>
                                    <td>{booking.bookDate}</td>
                                    <td><Link to={`/updatebookings/${booking.bookID}`}><button className="btn btn-outline-dark  ml-2">Update</button></Link></td>
                                </tr>
                            )) : <tr><td className="text-center" colSpan={6}>No Bookings Found or Server Down</td></tr>
                        }
                    </tbody>
                </Table>
        </div>
    )
}

export default AllBookings
