import axios from "axios";
import React, { useEffect } from "react";
import { useState } from "react";
import { toast } from "react-toastify";
import base_url from "../api/Service";

function AddBooking() {

    useEffect(() => {
        document.title = "Booking || Property Booking Management System"
    }, [])

    const [customers, setCustomer] = useState([])
    const [bookid, setBookId] = useState({})

    const getAllCustomerFromServer = () => {
    axios.get(`${base_url}/customers/`).then(
        (response) => {
            setCustomer(response.data)
        },
        (error) => {
            console.log(error);
        }
    );
    }

    const getBookID = () => {

        axios.get(`${base_url}/bookings/maxid`).then(
            (response) => {
                setBookId(response.data + 1)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down", {
                    toastId: "errortoast"
                });
            }
        );

    }

    useEffect(() => {
        getAllCustomerFromServer();
    }, [])


    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">

                    <div className="w-100">
                        <form className="justify-content-center text-center" action=" " method="post" id="booking_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Booking Details</h2>
                            </div>
                            <input name="BookID" placeholder="Booking ID" className="form-control m-3" disabled type="text" />

                            <div className="form-group ">
                                <select className="form-control m-3" id="customerlist">
                                    <option>Select Customer</option>

                                    {
                                        customers.length > 0 ? customers.map((customer, i) => (

                                            <option key={i}>{customer.custName}</option>

                                            
                                        )) : <option>Server Down or No Customer Found</option>
                                    }

                                </select>
                            </div>

                            <div className="form-group ">
                                <select className="form-control m-3" id="propertylist">
                                    <option>Select Property</option>
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </select>
                            </div>

                            <input name="PropRate" placeholder="Rate" className="form-control m-3" disabled type="text" />
                            <input name="BookingAmount" placeholder="Booking Amount" className="form-control m-3" type="text" />
                            <button type="submit" className="btn btn-outline-success">Book</button>
                        </form>
                    </div>

                </div>
            </div>
        </div>

    )
}

export default AddBooking
