import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";
import base_url from "../api/Service";

function UpdateCustomer() {

    useEffect(() => {
        document.title = "Update Customer || Property Booking Management System"
    }, [])

    const [customer, setCustomer] = useState({})
    const [oldCustomer, setOldCustomer] = useState({})
    const [cityList, setCityList] = useState({})

    const handleForm = (e) => {
        putDatatoServer(customer);
        e.preventDefault();
        window.location.reload(false);
    }

    const {updateCustid} = useParams();

    const putDatatoServer = (data) => {
        axios.put(`${base_url}/customers/${updateCustid}`, data).then(
            (response) => {
                toast.success("Customer Updated Successfully", {
                    toastId: "custaddedtoast"
                })
            },
            (error) => {
                console.log(error);
            }
        )
    }

    const getOldCustomerData = (updateCustid) => {

        axios.get(`${base_url}/customers/${updateCustid}`).then(
            (response) => {
                setOldCustomer(response.data)
            },
            (error) => {
                console.log(error);
            }
        );
    }

    const getCityList = () => {

        axios.get(`${base_url}/cities/`).then(
            (response) => {
                setCityList(response.data)
            },
            (error) => {
                console.log(error);
            }
        );
    }

    useEffect(() => {
        const {updateCustid} = useParams();
        getOldCustomerData(updateCustid);
        getCityList();
        
    }, [])

    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">

                    <div className="w-100">
                        <form className="justify-content-center text-center" onSubmit={handleForm} action="/" method="post" id="customer_update_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Customer Details</h2>
                            </div>
                            <input id="custID" placeholder="Customer ID" className="form-control m-3" type="text" disabled value={updateCustid} />
                            <input id="custName" placeholder="Customer's Name" className="form-control m-3" type="text" value={oldCustomer.custName} onChange={(e) => {
                                setCustomer({ ...customer, custName: e.target.value })
                            }} />
                            <input id="custMobNo" placeholder="Enter Phone Number" className="form-control m-3" type="tel" value={oldCustomer.custMobNo} onChange={(e) => {
                                setCustomer({ ...customer, custMobNo: e.target.value })
                            }} />
                            <input id="email" placeholder="Enter E-Mail Address" className="form-control m-3" type="email" value={oldCustomer.email} onChange={(e) => {
                                setCustomer({ ...customer, email: e.target.value })
                            }} />
                            <input id="custAddress" placeholder="Customer's Address" className="form-control m-3" type="text" value={oldCustomer.custAddress} onChange={(e) => {
                                setCustomer({ ...customer, custAddress: e.target.value })
                            }} />

                            <div className="form-inline row ">
                                <div className="col-6">
                                    <select className="form-control w-100 ml-3" id="custCity" value={oldCustomer.custCity} onChange={(e) => {
                                        setCustomer({ ...customer, custCity: e.target.value })
                                    }}>
                                        <option>Select City</option>

                                        {
                                            cityList.length > 0 ? cityList.map((city, i) => (

                                                <option key={i}>{city.city}</option>


                                            )) : <option>Server Down or No City Found</option>
                                        }

                                    </select>
                                </div>
                                <div className="col-6">
                                    <select className="form-control w-100 ml-3" id="state" value={oldCustomer.state} onChange={(e) => {
                                        setCustomer({ ...customer, state: e.target.value })
                                    }}>
                                        <option>Select State</option>
                                        <option>Maharashtra</option>
                                    </select>

                                </div>

                            </div>
                            <input id="pincode" placeholder="Pin Code" className="form-control m-3" type="text" value={oldCustomer.pincode} onChange={(e) => {
                                setCustomer({ ...customer, pincode: e.target.value })
                            }} />

                            <button type="submit" className="btn btn-outline-success mx-3">Update Customer</button>
                            <button type="reset" className="btn btn-outline-danger mx-3">Clear</button>
                        </form>
                    </div>

                </div>
            </div>
        </div>

    )
}

export default UpdateCustomer
