import axios from "axios";
import React, { useEffect, useState } from "react";
import { toast } from "react-toastify";
import base_url from "../api/Service";

function AddPayment() {

    useEffect(() => {
        document.title = "Add Payment || Property Booking Management System"
    }, [])

    const [payid, setPayId] = useState({})
    const [bookings, setBookings] = useState([])
    const [selectedbooking, setSelectedBooking] = useState([])
    const [payment, setPayment] = useState([])
    const [amountpaid, setAmountPaid] = useState([])

    const getAmountPaid = (bid) => {
        axios.get(`${base_url}/payments/getamountpaid/${bid}`).then(
            (response) => {
                console.log(response.data)
                setAmountPaid(response.data)

            },
            (error) => {
                console.log(error);
            }
        );
    }

    const getAllBookingsFromServer = () => {
        axios.get(`${base_url}/bookings/`).then(
            (response) => {
                setBookings(response.data)
            },
            (error) => {
                console.log(error);
            }
        );
    }

    const getPayID = () => {

        axios.get(`${base_url}/payments/maxid`).then(
            (response) => {
                setPayId(response.data + 1)
            },
            (error) => {
                console.log(error);
                toast.error("Server Down", {
                    toastId: "errortoast"
                });
            }
        );
    }

    const getSelectedBookingFromServer = (bid) => {
        axios.get(`${base_url}/bookings/${bid}`).then(
            (response) => {
                setSelectedBooking(response.data)

            },
            (error) => {
                console.log(error);
            }
        );
    }

    const handleSelect = () => {
        getSelectedBookingFromServer(payment.bookID);
        getAmountPaid(payment.bookID);
    }

    useEffect(() => {
        getPayID();
        getAllBookingsFromServer();
    }, [])

    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">

                    <div className="w-100">
                        <form className="justify-content-center text-center" action=" " method="post" id="payment_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Payment Details</h2>
                            </div>
                            <input name="PayID" placeholder="Payment ID" className="form-control m-3" disabled type="text" value={payid} />

                            <div className="form-group ">
                                <select className="form-control m-3" id="bookinglist" onClick={handleSelect} onChange={(e) => {
                                    setPayment({ ...payment, bookID: e.target.value })
                                }}>
                                    <option>Select Booking ID</option>
                                    {
                                        bookings.length > 0 ? bookings.map((booking, i) => (

                                            <option key={i} value={booking.bookID}>{booking.bookID}. {booking.cname}</option>

                                        )) : <option>Server Down or No Booking Found</option>
                                    }
                                </select>
                            </div>

                            <input name="CustName" disabled placeholder="Customer's Name" className="form-control m-3" defaultValue={selectedbooking.cname} type="text" />
                            <input name="PropRate" disabled placeholder="Rate" className="form-control m-3" type="text" defaultValue={selectedbooking.rate} />
                            <input name="AmountPaid" disabled placeholder="Amount Paid" className="form-control m-3" type="text" defaultValue={amountpaid}/>
                            <input name="AmountRemaining" disabled placeholder="Remaining Amount" className="form-control m-3" type="text" defaultValue={selectedbooking.rate-amountpaid}/>
                            <input name="AmountPaying" placeholder="Amount Paying Now" className="form-control m-3" type="text" />

                            <button type="submit" className="btn btn-outline-success">Add Payment</button>
                        </form>
                    </div>

                </div>
            </div>
        </div>

    )
}

export default AddPayment
