import React, { useEffect } from "react";
import { Table } from "reactstrap";


import base_url from "../api/Service";
import axios from 'axios';
import { toast } from "react-toastify";
import { useState } from "react";

function AllCustomers() {

    let custs = [
        {
            
                "email": "amol@gmail.com",
                "state": "Maharashtra",
                "pincode": "424001",
                "custID": 1,
                "custAddress": "Ramchandra Nagar",
                "custMobNo": "8983610053",
                "custCity": "Dhule",
                "custName": "Amol"
            
        },
        {
            
            "email": "Sudhanshu@gmail.com",
            "state": "Maharashtra",
            "pincode": "424001",
            "custID": 2,
            "custAddress": "Ramchandra Nagar",
            "custMobNo": "8983610053",
            "custCity": "Dhule",
            "custName": "Sudhanshu"
        
    }
    ]
    const [customers,setCustomer]=useState([
        {
            
                "email": "amol@gmail.com",
                "state": "Maharashtra",
                "pincode": "424001",
                "custID": 1,
                "custAddress": "Ramchandra Nagar",
                "custMobNo": "8983610053",
                "custCity": "Dhule",
                "custName": "Amol"
            
        },
        {
            
            "email": "Sudhanshu@gmail.com",
            "state": "Maharashtra",
            "pincode": "424001",
            "custID": 2,
            "custAddress": "Ramchandra Nagar",
            "custMobNo": "8983610053",
            "custCity": "Dhule",
            "custName": "Sudhanshu"
        
    }
    ])

    useEffect(() => {
        document.title = "Customer Report || Property Booking Management System"
    }, [])

    const getAllCustomerFromServer = () => {
        axios.get(`${base_url}/customers/`).then(
            (response) => {
                console.log(response);
                console.log(response.data);
                toast.success("Customers has been Loaded");
                custs = response.data
            },
            (error) => {
                console.log(error);
                toast.error("Something Went Wrong");
            }
        );
    };

    useEffect(() => {
        getAllCustomerFromServer();
    }, [])

    return (
        <div className="ml-3">
            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                <h2>Customers Reports</h2>
            </div>
            <Table className="m-3" striped >
                <thead>
                    <tr>
                        <th>Customer Id</th>
                        <th>Customer Name</th>
                        <th>Mobile Number</th>
                        <th>City</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        custs.map((customer)=>(
                        <tr>
                            <th scope="row">{customer.CustID}</th>
                            <td>{customer.CustName}</td>
                            <td>{customer.CustMobNo}</td>
                            <td>{customer.CustCity}</td>
                        </tr>
                        ))
                    }
                    

                    
                </tbody>
            </Table>
        </div>
    )
}

export default AllCustomers
