import React, { useEffect } from "react";
import { Table } from "reactstrap";


import base_url from "../api/Service";
import axios from 'axios';
import { toast } from "react-toastify";

function AllCustomers() {

    useEffect(() => {
        document.title = "Customer Report || Property Booking Management System"
    }, [])

    const getAllCustomerFromServer = () => {
        axios.get(`${base_url}/customers/`).then(
            (response) => {
                console.log(response);
                console.log(response.data);
                toast.success("Customers has been Loaded");

            },
            (error) => {
                console.log(error);
                toast.error("Something Went Wrong");
            }
        );
    };

    useEffect(() => {
        getAllCustomerFromServer();
    }, [])

    return (
        <div className="ml-3">
            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                <h2>Customers Reports</h2>
            </div>
            <Table className="m-3" striped >
                <thead>
                    <tr>
                        <th>Customer Id</th>
                        <th>Customer Name</th>
                        <th>Mobile Number</th>
                        <th>City</th>
                    </tr>
                </thead>
                <tbody>
                {data.map((item, i) => (
                    <tr key={i}>
                        <td>{item.CustID}</td>
                        <td>{item.CustName}</td>
                        <td>{item.CustMobNo}</td>
                        <td>{item.CustCity}</td>
                    </tr>
                ))}
                    
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Larry</td>
                        <td>the Bird</td>
                        <td>@twitter</td>
                    </tr>
                </tbody>
            </Table>
        </div>
    )
}

export default AllCustomers
