import axios from "axios";
import React, { useEffect, useState } from "react";
import base_url from "../api/Service";

function AddCustomer() {

    useEffect(() => {
        document.title = "Add Customer || Property Booking Management System"
    }, [])

    const [customer, setCustomer] = useState({})

    const handleForm = (e) => {
        console.log(customer);
        postDatatoServer(customer);
        e.preventDefault();
    }


    const postDatatoServer=(data)=>{
        axios.post(`${base_url}/customers/`,data).then(
            (response) => {
                console.log(response)
            },
            (error) => {
                console.log(error);
            }
        )
    }

    return (
        <div>
            <div className="container-fluid ">
                <div className="row col-12">

                    <div className="w-100">
                        <form className="justify-content-center text-center" onSubmit={handleForm} action=" " method="post" id="customer_form">

                            <div className="alert alert-info text-center w-100 p-3 ml-3" role="alert">
                                <h2>Customer Details</h2>
                            </div>
                            <input id="CustID" placeholder="Customer ID" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, CustID: e.target.value })
                            }}/>
                            <input id="CustName" placeholder="Customer's Name" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, CustName: e.target.value })
                            }} />
                            <input id="CustMobNo" placeholder="Enter Phone Number" className="form-control m-3" type="tel" onChange={(e) => {
                                setCustomer({ ...customer, CustMobNo: e.target.value })
                            }} />
                            <input id="email" placeholder="Enter E-Mail Address" className="form-control m-3" type="email" onChange={(e) => {
                                setCustomer({ ...customer, email: e.target.value })
                            }} />
                            <input id="CustAddress" placeholder="Customer's Address" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, CustAddress: e.target.value })
                            }} />
                            <input id="cust_city" placeholder="Enter City" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, cust_city: e.target.value })
                            }} />
                            <input id="state" placeholder="State" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, state: e.target.value })
                            }} />
                            <input id="Pincode" placeholder="Pin Code" className="form-control m-3" type="text" onChange={(e) => {
                                setCustomer({ ...customer, Pincode: e.target.value })
                            }} />

                            <button type="submit" className="btn btn-outline-success">Add Customer</button>
                        </form>
                    </div>

                </div>
            </div>
        </div>

    )
}

export default AddCustomer
